$(document).ready((function(){$("[data-bs-hover-animate]").mouseenter((function(){var a=$(this);
	a.addClass("animated "+a.attr("data-bs-hover-animate"))})).mouseleave((function(){var a=$(this);
		a.removeClass("animated "+a.attr("data-bs-hover-animate"))}))}));var btn=$("#scroll-to-top");
$(window).scroll((function(){$(window).scrollTop()>300?btn.addClass("show"):btn.removeClass("show")}));